mapper.select:
select count(*) 
from member 
where email = #{email}

mapper.update:
update address
set default_addr = 0
where no = #{no} 
and alias != #{alias} 
and addrDetail != #{addrDetail}
and roadaddrPart1 != '${roadaddrPart1}'

update address
set default_addr = 1
where no = #{no} 
and alias = #{alias} 
and addrDetail = #{addrDetail}
and roadaddrPart1 = '${roadaddrPart1}'
