mapper.select:
select count(*) 
from member 
where email = #{email}

mapper.update:
update address
set default_addr = 0
where no = #{no} 
and alias != #{alias} 
and addrDetail != #{addrDetail}
and roadaddrPart1 != '${roadaddrPart1}'

update address
set default_addr = 1
where no = #{no} 
and alias = #{alias} 
and addrDetail = #{addrDetail}
and roadaddrPart1 = '${roadaddrPart1}'

mapper.select:
select email, password, enabled, member.auth_no, auth
from member join authority on member.auth_no = authority.auth_no
where email = #{email}
